class App extends React.Component {
  constructor() {
    super()
    this.state = {
      counter: 1
    }
    // this.kasvataYhdella = this.kasvataYhdella.bind(this)  // sidotaan this-lause viittaamaan muodostettavaan objektiin
    // this.nollaa = this.nollaa.bind(this) // sidotaan this-lause viittaamaan muodostettavaan objektiin
  }

  // kun metodi määritetään näin luokka-attribuuttina (jolla arvoksi funktio), niin this kulkee mukana
  // tällöin this on niin sanottu "leksikaalinen this", koska se viittaa aina olioonsa
  asetaArvoon = (arvo) => () => this.setState({ counter: arvo })  // palautetaan funktio ikuisen luupin välttämiseksi



  render() {
    return (
      <div>
        <Display counter={this.state.counter}/>
        <div>
          <button onClick={this.asetaArvoon(this.state.counter+1)}>
            Plus
          </button>
          <button onClick={this.asetaArvoon(0)}>
            Zero
          </button>
        </div>
      </div>
    )
  }
}

const Display = ({ counter }) => <div>{counter}</div>

const Button = ({ handleClick, text }) => (
  <button onClick={handleClick}>
    {text}
  </button>
)




ReactDOM.render(
  <App />,
  document.getElementById('root')
)
